import ProductItem from "./ProductItem";
import classes from "./Products.module.css";
import { useState,useEffect } from "react"
const DUMMY_DATA = [
  {
    id: "d1",
    price: 5,
    title: "Meal",
    description: "My first meal i have ever made",
  },
  {
    id: "d2",
    price: 8,
    title: "Second Meal",
    description: "My second meal i have ever made",
  },
  {
    id:'d3',
    price:10,
    title:'Third Meal',
    description:'My third meal i have ever made'
  },
];

const Products = (props) => {

  const [meals, setMeals] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [httpError, setHttpError] = useState();
  useEffect(() => {
    setIsLoading(true);
    const fetchData = async () => {
      const response = await fetch(
        "https://react-example-http-8e1f8-default-rtdb.firebaseio.com/meals.json"
      );

      if (!response.ok) {
        throw new Error("Something Went Wrong!");
      }
      const responseData = await response.json();

      const loadedMeals = [];

      for (const key in responseData) {
        loadedMeals.push({
          id: key,
          name: responseData[key].name,
          description: responseData[key].description,
          price: responseData[key].price,
        });
      }
      setMeals(loadedMeals);
      setIsLoading(false);
    };

    fetchData().catch((error) => {
      setIsLoading(true);
      setHttpError(error.message); // Traditional promise only way of handling errors inside of a promise
    });
  }, []);

  return (
    <section className={classes.products}>
      <h2>Buy your favorite products</h2>
      <ul>
        {/* {DUMMY_DATA.map((product) =>(
          <ProductItem
          key={product.id}
          id={product.id}
          title={product.title}
          price={product.price}
          description={product.description}
        />
        ))} */}
        {meals}
      </ul>
      {isLoading && <p>...Loading</p>}
      {httpError && <p>Failed to Fetch.</p>}
    </section>
  );
};

export default Products;
